INSERT INTO uml_open_course_db.courseManager (name, id, message) VALUES ('侯爱民11', 1, '<p><span style="color: rgb(68, 68, 68); font-family: &quot;Times New Roman&quot;; font-size: 14px; text-align: justify; background-color: rgb(255, 255, 255);">侯爱民，男，976年3月出生。籍贯江西南昌。东莞理工学院计算机科学与技术学院讲师。研究生学历，博士学位。</span><br/><span style="color: rgb(68, 68, 68); font-family: &quot;Times New Roman&quot;; font-size: 14px; text-align: justify; background-color: rgb(255, 255, 255);">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;从2017-2018学年第1学期开始，至2018-2019学年第1学期止，总共为2届的学生讲授《UML面向对象分析与设计》课程。累计学生人数200，累计学时120。11</span></p>');
create table dguterd1_uml
(
  erd1_id      int auto_increment
    primary key,
  erd1_title   varchar(50) not null,
  erd1_type    varchar(20) not null,
  erd1_date    date        not null,
  erd1_origin  varchar(50) not null,
  erd1_content longtext    not null,
  erd1_state   int         not null
)
  collate = utf8_unicode_520_ci;

INSERT INTO uml_open_course_db.dguterd1_uml (erd1_id, erd1_title, erd1_type, erd1_date, erd1_origin, erd1_content, erd1_state) VALUES (10014, '关于加快高素质应用型人才培养体系建设全面提高教育教学质量的实施意见（2016 年 10 月 9 日）', '教学体系', '2017-12-04', '东莞理工学院教务处 ', '<p style="text-align: center;"><span style="font-size: 20px; color: rgb(255, 0, 0);">关于加快高素质应用型人才培养体系建设全面提高教育教学质量的实施意见（2016 年 10 月 9 日）</span><span style="font-size: 20px;"><br/></span></p><p><span style="font-size: 20px;"></span></p><p style="margin-right:26px;margin-left:24px;text-indent:72px;text-align:justify;text-justify:inter-ideograph;line-height:23px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">为贯彻落实《教育部关于全面提高高等教育质量的若干意见》精神，着力构建高素质应用型人才培养体系，深化教育教学改革，加快推进学校高水平理工科大学建设，特提出如下实施意见。</span></span> </p><p style="line-height:13px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="line-height:22px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-left:56px;line-height:18px"><strong><span style=";font-family:宋体;font-weight:bold;font-size:16px"><span style="font-family:宋体">一、指导思想与总体目标</span></span></strong> </p><p style="line-height:8px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-left:56px;line-height:18px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">（一）指导思想</span></span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:23px;margin-left:24px;text-indent:32px;text-align:justify;text-justify:inter-ideograph;line-height:25px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">全面贯彻落实中央和省委、省政府关于教育教学改革的部署和要求，围绕立德树人根本任务，坚持教学为中心，质量是生命，创新是灵魂的理念；牢固树立</span>“以卓越的创新教育与实践造福社会”的价值追求，着力推进科教融合、创新育人，促进学生全面发展；加快内涵发展，立足学校办学优势与特色，以“成果导向”引导教育教学改革，加强教学管理工作，不断提高办学水平和人才培养质量。</span> </p><p style="line-height:9px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-left:56px;line-height:18px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">（二）总体目标</span></span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:26px;margin-left:24px;text-indent:32px;line-height:25px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">通过师资队伍建设、专业结构调整优化、教学管理体制改革、数字化教学资源建设、实践教学体系建设、教风学风建设、教学质量评价与激励机制完善、国际化战略推进等一系列改革与建设举措的实施，到</span> 2020 年，全面形成特色鲜明的高素质应用型人才培养模式、严格规范的教学管理体系、符合创新实践能力培养目标的实践教学体系、科学有效的教学质量监控与保障机制，形成一批教学改革成果和品牌特色专业，力争学校本科教学水平和人才培养质量位居国内同类高校前列。</span> </p><p style="line-height:13px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="line-height:22px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-left:56px;line-height:18px"><strong><span style=";font-family:宋体;font-weight:bold;font-size:16px"><span style="font-family:宋体">二、建设任务</span></span></strong> </p><p style="line-height:8px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-left:56px;line-height:18px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">（一）优化专业结构，加强专业评估和专业认证</span></span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:23px;margin-left:24px;text-indent:32px;text-align:justify;text-justify:inter-ideograph;line-height:25px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">按照高水平理工科大学建设目标，积极稳妥地调整专业设置，优化专业结构，逐步提高理工科专业的比例。工科专业以通过国家工程教育专业认证为建设目标，人文社科专业以专业综合改革为导向积极申报和建设各级品牌专业特色专业。以人才培养方案修订、培养标准的精细化、课程体系优化与教学内容改革为重点，分层次、分类别、有计划地开展专业建设。争取</span> 5 年内，现有工科专业中的 50%通过专业认证。</span> </p><p><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"><br/> </span> &nbsp; &nbsp;</p><p style="line-height:13px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="line-height:20px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style=";text-align:center"><span style=";font-family:Times;font-size:16px">71</span> </p><p><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"><br/> </span> &nbsp; &nbsp;</p><p style="line-height:8px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:23px;margin-left:24px;text-indent:32px;text-align:justify;text-justify:inter-ideograph;line-height:25px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">建立专业评估制度和专业准入与退出机制。对学科水平低、办学条件差、教学管理不善及招生就业困难的专业，实行减少招生或停止招生；按照优势突出、特色鲜明、新兴交叉、社会急需的原则，全面启动重点专业和特色专业建设，通过重点投入、重点建设、精心打造，建设一批具有示范和辐射作用的优势专业，带动学校专业水平的全面提升。采取多种措施加大新办专业的建设力度，提升学校新专业水平。</span></span> </p><p style="line-height:9px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-left:56px;line-height:18px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">（二）围绕高素质应用型人才培养目标，优化人才培养方案和课程体系</span></span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:23px;margin-left:24px;text-indent:32px;text-align:justify;text-justify:inter-ideograph;line-height:24px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">各专业通过分层次对标和广泛调研，精确定位专业培养目标，细化分解培养目标中包含的知识、能力、素质要求，根据本专业培养目标和毕业要求反向推导修订人才培养方案。以学习成果为导向调整优化课程体系，构建</span>“厚基础、强实践”的专业课程体系。</span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:23px;margin-left:24px;text-indent:32px;text-align:justify;text-justify:inter-ideograph;line-height:25px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">加强课程的常规性建设和规范化管理，设立课程建设专项经费，选择基础条件好、学生受益面广的课程进行重点建设，力争建成一批示范性强，辐射面广、影响力大的国家级、省级和校级精品课程，带动全校课程建设水平的整体提高。进一步缩减学分学时，增大实践教学学分比例，增开各类选修课。将创新创业教育纳入人才培养方案的目标和要求，在课程体系中融入创新创业教育的理念、内容和实践，开发创新创业新课程，使创新创业教育在整个课程体系中的重要性得到明显体现。</span></span> </p><p style="line-height:9px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-left:56px;line-height:18px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">（三）深化实验实践教学体系改革，加强实验技术队伍建设</span></span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:23px;margin-left:24px;text-indent:32px;text-align:justify;text-justify:inter-ideograph;line-height:25px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">全面规划和设计实验实践教学体系，建立一批资源共享的、以学科或学科群为基础的跨学院跨专业的实验教学示范中心。进一步深化实验实践教学内容改革，构建由基础实验、专业实验、综合设计实验和创新实验组成的实验教学体系；加强实验室和实习实训基地建设，吸纳社会优质资源，着力推进与行业著名公司、龙头企业共建追踪最新技术的创新实验室和实验教学、工程训练和创新训练、创业训练平台，营造</span>“真实”、“集成”的训练环境，强化学生实践能力培养。</span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:23px;margin-left:24px;text-indent:32px;text-align:justify;text-justify:inter-ideograph;line-height:24px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">建立和完善实验室校院两级管理体制，建立健全规章制度，规范创新实验教学管理；加大教学实验室和科研实验室的开放力度，激励教师吸纳本科生参加科研项目，加强学生的科研训练和创业训练；加强对实践教学环节的质量监控。</span></span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:23px;margin-left:24px;text-indent:32px;text-align:justify;text-justify:inter-ideograph;line-height:23px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">大力加强实验技术队伍建设，根据二级学院专业设置情况，合理增加实验员数量。建立健全实验技术人员的发展与晋升体系，着力建设一支有利于人才培养、学科建设和科技创新，高质量地服务于全校教学科研的实验技术队伍。</span></span> </p><p style="line-height:8px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-left:56px;line-height:18px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">（四）完善教学管理组织构架，提高教学管理水平</span></span> </p><p><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"><br/> </span> &nbsp; &nbsp;</p><p style="line-height:13px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="line-height:13px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="line-height:25px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style=";text-align:center"><span style=";font-family:Times;font-size:16px">72</span> </p><p><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"><br/> </span> &nbsp; &nbsp;</p><p style="line-height:8px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:23px;margin-left:24px;text-indent:32px;text-align:justify;text-justify:inter-ideograph;line-height:24px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">建立校、院、系（课程群）三级教学管理组织架构，明确教学管理职责和目标，扩大二级学院教学管理自主权，建立起以学院为主体的教学管理体系，确保责权利相统一。建立制约和激励并存的奖惩机制，增强基层教学管理活力，提高教学管理效率，确保学校总体教学目标的实现。</span></span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:23px;margin-left:24px;text-indent:32px;text-align:justify;text-justify:inter-ideograph;line-height:25px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">按照学校定岗定编的总体要求，根据应用型人才培养目标对于教学管理的功能需求，调整和完善教学管理机构科室建制，优化院系教学管理队伍构架，根据本科专业数、学生数增设教务员，保证教学管理队伍的数量。加强教学管理人员学习培训，了解教学改革最新动态、掌握教学管理基本知识，提高教学管理水平。</span></span> </p><p style="line-height:9px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-left:56px;line-height:18px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">（五）建立教学质量责任人制度，健全教学质量评价组织机构</span></span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:23px;margin-left:24px;text-indent:32px;text-align:justify;text-justify:inter-ideograph;line-height:25px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">各级党政主要领导要亲自抓教学质量，校党委书记、校长是学校教学质量的第一责任人，分管教学的副校长、分管学生工作的副书记是学校教学质量的主要责任人；教务处、质量监控与评估中心、学生工作部（处）、学院党政负责人是学校教学质量的直接责任人；学校各部处、各部门、各级党群组织的工作都应服务于教学中心工作。二级学院是教学工作的实施单位和教学管理与教学建设的主体，学院党政负责人为学院教学质量第一责任人；系主任（课程群负责人）是专业和课程教学质量的第一责任人；教师是课程教学质量的直接责任人。</span></span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:23px;margin-left:24px;text-indent:32px;text-align:justify;text-justify:inter-ideograph;line-height:25px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">加强对教学工作的管理和督察。教务处在教学管理工作中要充分发挥规划、协调、引导和服务的职能，制定教学基本建设与改革规划，完善教学管理规章制度，为院系教学工作提供支持和服务。质量监控与评估中心负责制定和完善专业、课程、主要教学环节的质量评价标准，强化教学质量过程与目标管理；引入第三方评估与评价，全面客观反映教学质量状况；加强高等教育研究和研究成果积累，为教学质量改进提供理论支撑。</span></span> </p><p style="line-height:9px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-left:56px;line-height:18px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">（六）多渠道补充优质师资，加快师资队伍建设</span></span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:23px;margin-left:24px;text-indent:32px;text-align:justify;text-justify:inter-ideograph;line-height:24px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">根据学校高水平理工科大学建设规划要求，针对现阶段专业教学师资短缺的现实，分年度有计划地精准引育人才。到</span> 2020 年，引进高水平博士、境外知名大学优质师资、企业产业精英师资共 400 名，打造一支数量充足、结构合理、素质优秀的师资队伍，确保高水平应用型人才培养工作有序、有效运行。</span> </p><p style="line-height:9px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-left:56px;line-height:18px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">（七）完善教学评价制度，改革教学绩效考核方式</span></span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:23px;margin-left:24px;text-indent:32px;text-align:justify;text-justify:inter-ideograph;line-height:24px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">建立科学、完整、操作性强的教学质量评价体系，定期采集和公布二级学院教学基本状态信息数据；加大对教学评价结果的奖惩力度，引导和激励教师不断提高教学质量；进一步完善同行专家和校、院两级领导听课评价制度，期中教学检查制度，教学专项评估制度，课堂教学评教制度，教学评奖制度，毕</span></span> </p><p><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"><br/> </span> &nbsp; &nbsp;</p><p style="line-height:13px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="line-height:13px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="line-height:25px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style=";text-align:center"><span style=";font-family:Times;font-size:16px">73</span> </p><p><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"><br/> </span> &nbsp; &nbsp;</p><p style="line-height:5px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-left:24px;line-height:18px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">业生追踪调查制度等，确保教学质量的稳步提高。</span></span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:23px;margin-left:24px;text-indent:32px;text-align:justify;text-justify:inter-ideograph;line-height:25px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">由质量监控与评估中心制定教学质量评价办法，协同人事考核部门出台专门细则，将原来以课时数为主要考核依据的教学绩效考核方式转变为由教学质量和课时数综合的考核方式，充分激励院系、教师全身心投入教学，提高教学质量。通过教学状态数据采集填报，开展二级学院、专业、课程、主要教学环节评估，编制和发布校院教学质量报告，定期发布校、院教学质量指数，将教师个人教学质量评价系数、二级学院教学质量指数作为绩效工资发放和学校教学资源配置的定量依据，形成科学完善、执行有力的学校内部教学质量监测与保障体系。</span></span> </p><p style="line-height:8px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-left:56px;line-height:18px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">（八）建设数字化教学平台，服务师生互动和学生自主学习</span></span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:23px;margin-left:24px;text-indent:32px;text-align:justify;text-justify:inter-ideograph;line-height:25px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">建设数字化教学平台，创建有利于学生素质教育、创新创业意识培养、实践能力提升的数字化教学资源。实施重点课程、精品课程、品牌专业必修课程、实验教学示范中心资源平台和文化素质教育的立体化教学资源建设，以精品课程、品牌专业和文化素质教育数字化建设为突破口，建成既能满足学生自主学习、专业知识需要又能拓展学生素质教育的数字化课程中心。到</span> 2020 年，建成涵盖所有专业和各类课程的学生自主学习、文化素质教育、师生互动的数字化资源平台。</span> </p><p style="line-height:9px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-left:56px;line-height:18px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">（九）加强教风学风建设，形成良好校风</span></span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:23px;margin-left:24px;text-indent:32px;text-align:justify;text-justify:inter-ideograph;line-height:25px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">以制度建设为基础，以强化管理为手段，狠抓教师教学工作规范和教学技能提升，不断提高教学水平；发挥宣传媒体的正面教育引导作用，加大对爱岗敬业、潜心育人的优秀教师先进事迹的宣传报道，加强对国家级、省部级教学成果奖，教学名师，精品课程等典型成果的宣传报道，营造良好的尊师重教的氛围；强化全员育人意识，加强教学督导队伍建设，增强教学服务人员的责任意识、服务意识和业务能力，不断提高服务师生质量，以优良教风促进学风建设。依托学科竞赛等活动拓展第二课堂，以综合实践促进优良学风形成。</span></span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:23px;margin-left:24px;text-indent:32px;text-align:justify;text-justify:inter-ideograph;line-height:25px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">加强校纪校规教育、诚信教育，严格课堂教学纪律管理和违纪处理，狠抓学风、考风建设；完善领导干部联系班级制度、管理干部听课制度、学生辅导员学习访谈制度、班主任班级例会制度；改革课程考核方法，注重学生学习过程考核，提倡考核形式多样化；建立学生学业导师制度，拓宽师生交流互动等途径，促进良好学风的形成。</span></span> </p><p style="line-height:9px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-left:56px;line-height:18px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">（十）强化国际化战略，提高国际化办学水平和层次</span></span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:34px;margin-left:24px;text-indent:32px;line-height:23px"><span style=";font-family:宋体;font-size:15px"><span style="font-family:宋体">把学生国际化培养作为一件带有根本性、全局性和长远性的大事抓紧推进，充分利用国际优质教育资源，加大国际化培养人才的力度。采取走出去，请进来的办法，加强国际合作，积极探索与国外著名大学、国际性大公司合作</span></span> </p><p><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"><br/> </span> &nbsp; &nbsp;</p><p style="line-height:13px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="line-height:13px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="line-height:25px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style=";text-align:center"><span style=";font-family:Times;font-size:16px">74</span> </p><p><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"><br/> </span> &nbsp;</p><p style="line-height:8px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span></p><p style="margin-right:26px;margin-left:24px;text-align:justify;text-justify:inter-ideograph;line-height:25px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">的国际化联合培养新模式，选派优秀学生赴国外著名大学交流和学习，开展学生的短期实习、游学、暑期学分、比赛、海外实训，扩大学生视野，增长学生见识。加大全英课程建设力度，吸引外国留学生来校学习、交流；将教师送出海外培训、访学、留学深造，开展学术交流与项目合作；积极邀请国际知名专家、学者、名人等来校举办多种文化主题活动和讲座；力争引进</span> 20—30 位高端外籍专业教师，大幅度增加有海外学习和国际交流经历的在校教师比例；积极引进优质教育资源，申报中外合作办学机构（项目），努力提升国际合作办学的水平和层次。</span> </p><p style="line-height:13px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span></p><p style="line-height:22px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span></p><p style="margin-left:56px;line-height:18px"><strong><span style=";font-family:宋体;font-weight:bold;font-size:16px"><span style="font-family:宋体">三、保障措施</span></span></strong> </p><p style="line-height:8px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span></p><p style="margin-left:56px;line-height:18px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">（一）加大经费投入</span></span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span></p><p style="margin-right:23px;margin-left:24px;text-indent:32px;text-align:justify;text-justify:inter-ideograph;line-height:25px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">进一步完善和落实教学经费保障机制，加大经费投入，学校新增经费优先保障教学建设和教学改革需要。充分调动校内外各种优质教学资源，将教学经费和资源优化配置到高素质应用型人才培养体系建设的各个主体、各个环节，通过教改立项、校企共建、成果奖励、教学竞赛、奖教助学等方式，形成有力支撑高素质应用型人才培养体系建设的资金保障体系和功能协同机制。</span></span> </p><p style="line-height:9px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span></p><p style="margin-left:56px;line-height:18px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">（二）出台配套细则</span></span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span></p><p style="margin-right:23px;margin-left:24px;text-indent:32px;text-align:justify;text-justify:inter-ideograph;line-height:24px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">按照学校统一部署，出台教学改革政策配套细则和行动计划，教学管理职能部门和各教学单位要结合部门职责、本学院的学科专业特点及发展建设规划，制定相应的建设任务实施细则，进一步明确各级单位的任务要求和责任义务，建立协同联动机制，推进各项改革措施和建设任务的实施。</span></span> </p><p style="line-height:9px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span></p><p style="margin-left:56px;line-height:18px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">（三）强化激励措施</span></span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span></p><p><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">强化目标管理，加强绩效评价，将高素质应用型人才培养体系建设和实施作为重要绩效考核指标。加大对在应用型人才培养体系建设和教育教学改革中做出重大贡献的优秀教学团队和优秀教师奖励力度，在职称评聘中实行</span>“教学考核一票否决制”。加大对有良好基础、组织申报国家级和省级教学成果奖的教学团队的扶持和资助力度，加大各级教学成果奖励力度。明确应用型人才培养体系建设的主体责任，建立严格的考评体系和问责机制，设定建设任务的时间表和路线图，确保各项建设任务高质高效完成。</span></p><p><span style="font-size: 20px;"></span><br/></p>', 1);
INSERT INTO uml_open_course_db.dguterd1_uml (erd1_id, erd1_title, erd1_type, erd1_date, erd1_origin, erd1_content, erd1_state) VALUES (10015, '东莞理工学院“十三五”专业建设与发展规划（2016—2020 年） ', '教学体系', '2017-12-05', '东莞理工学院教务处', '<p style="text-align: center;"><span style="font-size: 20px; color: rgb(255, 0, 0);">东莞理工学院“十三五”专业建设与发展规划（2016—2020 年）</span></p><p><span style="font-size: 20px; color: rgb(255, 0, 0);"></span></p><p>专业建设是高等学校重要的教学基本建设，从根本上反映了高校的办学水平、办学特色和核心竞争力。“十三五”时期是学校的重要战略机遇期和转型时期，是加强专业建设，提升学校综合实力和办学水平的关键时期。为进一步优化专业结构，充分发挥专业建设在人才培养、科学研究、社会服务中的引领作用，实现教学质量全面、协调、可持续发展，加快推进学校高水平理工科大学建设，根据《国家中长期教育改革和发展规划纲要（2010—2020
 
年）》《普通高等学校本科专业设置的规定》《广东省先进制造业发展“十三五”规划》《广东省教育厅东莞市人民政府共建东莞理工学院协议》《东莞市国民经济和社会发展第十三个五年规划纲要》《东莞理工学院高水平理工科大学建设总体规划（2015—
 2020）》等文件精神，结合学校发展实际，特制定本规划。</p><p><br/> </p><p><br/> </p><p>一、专业建设与发展现状</p><p><br/> </p><p>（一）专业结构与布局</p><p><br/> </p><p>东莞理工学院于 1990 年筹办，1992 年 4 月经原国家教委批准成立，2002 年 3 
月经教育部批准变更为本科全日制普通高等院校，2006 年 5 月获批成为学士学位授予单位，2008 年 5 
月提前参加教育部本科教学工作水平评估并以良好成绩通过。学校充分利用既有教育资源，优化学科专业结构，拓宽专业口径，以社会需求为导向，合理设置专业，初步形成了多学科稳步发展的专业结构与布局。到目前为止，学校设有
 39 个普通本科专业（详见表 1），共涉及工学、管理学、文学、理学、经济学院、法学、教育学、艺术学等 8 个学科门类，其中，理工类专业 20 
个，所占的比例为 51.3%（详见表 2）。学校的专业设置能够遵循教育规律，充分利用教育资源，基本符合社会发展和经济建设对人才培养的需求。</p><p><br/> </p><p><span style="font-size: 20px; color: rgb(255, 0, 0);"><br/></span><br/></p>', 1);
INSERT INTO uml_open_course_db.dguterd1_uml (erd1_id, erd1_title, erd1_type, erd1_date, erd1_origin, erd1_content, erd1_state) VALUES (10016, '东莞理工学院特色专业建设实施办法 ', '教学方式', '2017-12-05', '东莞理工学院教务处', '<p style="text-align: center;"><span style="font-size: 24px; color: rgb(255, 0, 0);">东莞理工学院特色专业建设实施办法</span></p><p><span style="font-size: 24px; color: rgb(255, 0, 0);"></span></p><p style="margin-right:8px;margin-left:24px;text-indent:32px;line-height:24px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">为进一步提升我校办学水平和综合实力，加快高水平理工科大学的建设步伐，突出人才培养的办学优势和特色，满足经济建设与社会发展对复合型、应用型人才的需求，根据《教育部财政部关于实施高等学校本科教学质量与教学改革工程的意见》（教高〔</span>2007〕1 号）的文件精神，特制定本办法。</span> </p><p style="line-height:13px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="line-height:22px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-left:56px;line-height:18px"><strong><span style=";font-family:宋体;font-weight:bold;font-size:16px"><span style="font-family:宋体">一、特色专业建设的指导思想</span></span></strong> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:16px;margin-left:24px;text-indent:32px;text-align:justify;text-justify:inter-ideograph;line-height:25px"><span style=";font-family:宋体;font-size:16px">1、按照“理念先进、目标明确、改革领先、师资优化、设备精良、教学优秀”的要求，科学规划，强力实施，创新机制，加大投入，深化教学改革，合理配置资源，使我校一些本科专业在办学条件、建设水平、管理水平、教学改革成果、科研水平以及人才培养质量等方面形成优势和特色，全面提升我校办学质量，扩大社会知名度。</span> </p><p style="line-height:13px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="line-height:22px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-left:56px;line-height:18px"><strong><span style=";font-family:宋体;font-weight:bold;font-size:16px"><span style="font-family:宋体">二、特色专业建设的总体目标</span></span></strong> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:24px;margin-left:24px;text-indent:32px;text-align:justify;text-justify:inter-ideograph;line-height:24px"><span style=";font-family:宋体;font-size:16px">2、在现有专业建设成果的基础上，全面实施特色专业建设工程，通过遴选培育，重点打造一批优势明显特色鲜明的专业。在现有 5 个国家级、省级特色专业的基础上，通争取在 2017 年建成 8 个省级以上重点特色专业，2020 年建成 10 个省级以上重点特色专业。</span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:24px;margin-left:24px;text-indent:32px;text-align:justify;text-justify:inter-ideograph;line-height:23px"><span style=";font-family:宋体;font-size:16px">3、特色专业要达到教育教学理念先进、人才培养措施适应时代要求、专业建设、教学改革、管理水平和人才培养质量在校内达到领先水平、在省内具有较高社会声誉的目标。</span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:24px;margin-left:24px;text-indent:32px;text-align:justify;text-justify:inter-ideograph;line-height:23px"><span style=";font-family:宋体;font-size:16px">4、通过重点特色专业建设，进一步优化学校学科专业结构，提升学校专业建设的整体水平，发挥 “以点带面”的示范作用，提高学校人才培养的质量、效益和在省内外的竞争力。</span> </p><p style="line-height:13px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="line-height:22px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-left:56px;line-height:18px"><strong><span style=";font-family:宋体;font-weight:bold;font-size:16px"><span style="font-family:宋体">三、特色专业建设的遴选标准及条件</span></span></strong> </p><p style="line-height:8px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-left:56px;line-height:18px"><span style=";font-family:宋体;font-size:16px">5、校级特色专业应具有 3 届以上毕业生。</span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:18px;margin-left:24px;text-indent:32px;text-align:justify;text-justify:inter-ideograph;line-height:24px"><span style=";font-family:宋体;font-size:16px">6、近两年的专业评估达到良好及以上等级。专业建设方案应具有先进的办学理念、明确的建设目标、清晰的改革思路。人才培养目标符合社会发展需要，注重知识、能力、素质协调发展，注重创新精神、实践能力和创业能力的培养。人才培养方案具有创新性、科学性、可操作性。</span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:24px;margin-left:24px;text-indent:32px;line-height:21px"><span style=";font-family:宋体;font-size:15px">7、教师队伍相对稳定。拥有具备较高的教学科研水平和学术知名度的骨干教师，专业师资队伍职称结构、学历结构和年龄结构合理；队伍建设规划科学合</span> </p><p><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"><br/> </span> &nbsp; &nbsp;</p><p style="line-height:13px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="line-height:23px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style=";text-align:center"><span style=";font-family:Times;font-size:16px">85</span> </p><p><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"><br/> </span> &nbsp; &nbsp;</p><p style="line-height:5px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-left:24px;line-height:18px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">理、措施得力、效果显著，有良好的科研背景或专业技术背景。</span></span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:18px;margin-left:24px;text-indent:32px;text-align:justify;text-justify:inter-ideograph;line-height:23px"><span style=";font-family:宋体;font-size:16px">8、按照地方经济社会对复合型、应用型人才培养要求，深化教学改革，优化课程体系，加强教材建设，更新教学内容，积极组织多媒体教学和双语教学，切实加强实验、实践教学环节。</span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:24px;margin-left:24px;text-indent:32px;text-align:justify;text-justify:inter-ideograph;line-height:23px"><span style=";font-family:宋体;font-size:16px">9、专业的教学基础设施条件良好，经费投入有足够保证，校内外的实验、实习、实训条件能够满足实践教学的要求，与机关、事业单位、行业、企业等有比较密切的联系和有效合作。</span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:16px;margin-left:56px;line-height:21px"><span style=";font-family:宋体;font-size:16px">10、重视教学管理改革，建立切实有效的质量保证和监控体系。11、人才培养质量较高，综合素质良好。毕业生就业率高，毕业生满意度高，</span> </p><p style="line-height:8px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-left:24px;line-height:18px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">用人单位对毕业生的综合评价好。</span></span> </p><p style="line-height:13px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="line-height:22px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-left:56px;line-height:18px"><strong><span style=";font-family:宋体;font-weight:bold;font-size:16px"><span style="font-family:宋体">四、特色专业遴选程序</span></span></strong> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:24px;margin-left:56px;line-height:21px"><span style=";font-family:宋体;font-size:16px">12、二级学院按照相关要求提交申报材料。13、学校组织专家组对申报材料进行评审，必要时，通知有关人员到场进行</span> </p><p style="line-height:9px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-left:24px;line-height:18px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">答辩，采取无记名投票方式择优遴选。</span></span> </p><p style="line-height:8px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-left:56px;line-height:18px"><span style=";font-family:宋体;font-size:16px">14、专家组评审的结果提交学校教学指导委员会审议。</span> </p><p style="line-height:9px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-left:56px;line-height:18px"><span style=";font-family:宋体;font-size:15px">15、教指委审议通过结果经学校审核批准后，纳入校级教学研究与改革项目，</span> </p><p style="line-height:8px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-left:24px;line-height:18px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">正式发文公布。</span></span> </p><p style="line-height:13px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="line-height:22px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-left:56px;line-height:18px"><strong><span style=";font-family:宋体;font-weight:bold;font-size:16px"><span style="font-family:宋体">五、特色专业的建设与管理</span></span></strong> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:16px;margin-left:24px;text-indent:32px;text-align:justify;text-justify:inter-ideograph;line-height:25px"><span style=";font-family:宋体;font-size:15px">16、特色专业建设实行系主任负责制，凡是被省、校建设的特色专业均视为省、校级重大教学改革课题。系主任负责主持拟订专业建设目标、制订专业建设规划，落实专业建设方案和人才培养方案。对照特色专业建设的目标、要求和验收标准，加强师资、课程、教材、实验实习实训基地建设，深化教学内容、课程体系和人才培养模式改革，落实责任，强化管理，保证达到专业建设的预期目标。</span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:24px;margin-left:24px;text-indent:32px;text-align:justify;text-justify:inter-ideograph;line-height:24px"><span style=";font-family:宋体;font-size:16px">17、校级特色专业自立项之日起，由学校专门划拨经费资助建设。每个专业资助 &nbsp;10 万元，按中期检查的效果，分两次资助。经费管理办法参照《东莞理工学院教学专项经费使用暂行办法》（莞工[2015]32 &nbsp;号）执行。如被立项为省级以上特色专业建设项目，学校将再追加建设经费。</span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:24px;margin-left:56px;line-height:21px"><span style=";font-family:宋体;font-size:16px">18、特色专业的建设周期一般为 2－3 年。19、学校要加大对特色专业建设的监督和检查，每年组织专家检查。同时加</span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:24px;margin-left:24px;line-height:21px"><span style=";font-family:宋体;font-size:15px"><span style="font-family:宋体">大支持和管理力度，通过大力扶持和政策导向，充分调动教师、教学管理人员和研究人员投身专业建设与改革的积极性，为特色专业的建设创造良好的环境。</span></span> </p><p style="line-height:8px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-left:56px;line-height:18px"><span style=";font-family:宋体;font-size:16px">20、特色专业建设完成目标和任务，或已满建设周期后，由所在院系向学校</span> </p><p><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"><br/> </span> &nbsp; &nbsp;</p><p style="line-height:13px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="line-height:13px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="line-height:25px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style=";text-align:center"><span style=";font-family:Times;font-size:16px">86</span> </p><p><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"><br/> </span> &nbsp;</p><p style="line-height:8px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span></p><p style="margin-right:24px;margin-left:24px;line-height:21px"><span style=";font-family:宋体;font-size:15px"><span style="font-family:宋体">提出书面申请，并对申请验收专业的建设、改革情况进行全面总结，提交自评报告。学校组织专家对申请验收的专业进行实地考察，依照验收标准进行验收。</span></span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span></p><p style="margin-right:16px;margin-left:24px;text-indent:32px;text-align:justify;text-justify:inter-ideograph;line-height:24px"><span style=";font-family:宋体;font-size:16px">21、验收合格者，由学校授予“东莞理工学院特色专业”称号，向社会公布。特色专业荣誉称号有效期 5 年，有效期内由学校向上推荐争取申报国家级省级特色专业。未通过验收的，一年内可申请再次验收，验收仍然不合格，视作未通过验收。</span> </p><p style="line-height:13px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span></p><p style="line-height:22px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span></p><p><strong><span style=";font-family:宋体;font-weight:bold;font-size:16px"><span style="font-family:宋体">六、本实施办法自发布之日起施行，教务处负责解释。</span></span></strong></p><p><span style="font-size: 24px; color: rgb(255, 0, 0);"></span><br/></p>', 1);
INSERT INTO uml_open_course_db.dguterd1_uml (erd1_id, erd1_title, erd1_type, erd1_date, erd1_origin, erd1_content, erd1_state) VALUES (10017, '东莞理工学院新建本科专业申报规定 ', '', '2018-12-05', '东莞理工学院教务处', '<p style="text-align: center;"><span style="font-size: 24px; color: rgb(255, 0, 0);">东莞理工学院新建本科专业申报规定</span></p><p><span style="font-size: 24px; color: rgb(255, 0, 0);"></span></p><p style="margin-right:16px;margin-left:24px;text-indent:32px;line-height:21px"><span style=";font-family:宋体;font-size:15px"><span style="font-family:宋体">为更好地适应地方社会经济发展需求，促进我校提高教育教学质量和办出特色，加大专业调整力度，优化人才培养结构，现就新建专业的申报提出以下规定：</span></span> </p><p style="line-height:19px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-left:56px;line-height:18px"><strong><span style=";font-family:宋体;font-weight:bold;font-size:16px"><span style="font-family:宋体">一、申报新专业的前期工作</span></span></strong> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:18px;margin-left:24px;text-indent:32px;text-align:justify;text-justify:inter-ideograph;line-height:24px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">认真做好拟新建专业的规划和人才需求调研。各二级教学单位做好各项调研工作，对国家、地区社会经济发展、全国、产业发展、对人才需求情况、本地区本专业的建设情况、毕业生就业情况、未来发展趋势分析等进行细致调查研究。根据学校的办学优势和特色，认真制订好学科发展和专业设置规划。</span></span> </p><p style="line-height:19px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-left:56px;line-height:18px"><strong><span style=";font-family:宋体;font-weight:bold;font-size:16px"><span style="font-family:宋体">二、申报专业必须具备的基本条件</span></span></strong> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:24px;margin-left:24px;text-indent:32px;text-align:justify;text-justify:inter-ideograph;line-height:23px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">按照《普通高等学校本科专业目录</span>(2012 年)》和《普通高等学校本科专业设置管理规定》，结合我校创新发展战略规划要求，申报新专业须符合以下基本条件：</span> </p><p style="line-height:9px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-left:56px;line-height:18px"><span style=";font-family:宋体;font-size:16px">1、符合学校办学定位和发展规划；</span> </p><p style="line-height:8px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-left:56px;line-height:18px"><span style=";font-family:宋体;font-size:16px">2、有相关学科专业为依托；</span> </p><p style="line-height:8px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-left:56px;line-height:18px"><span style=";font-family:宋体;font-size:16px">3、与地方经济社会发展适应度高，有稳定的社会人才需求；</span> </p><p style="line-height:8px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-left:56px;line-height:18px"><span style=";font-family:宋体;font-size:16px">4、有科学、规范、可行的专业人才培养方案；</span> </p><p style="line-height:8px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-left:56px;line-height:18px"><span style=";font-family:宋体;font-size:16px">5、有完成专业人才培养方案所必需的专职教师队伍及教学辅助人员。</span> </p><p style="line-height:8px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-left:56px;line-height:18px"><span style=";font-family:宋体;font-size:16px">6、有支持新专业建设发展的相应条件及投入保障。</span> </p><p style="line-height:19px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-left:56px;line-height:18px"><strong><span style=";font-family:宋体;font-weight:bold;font-size:16px"><span style="font-family:宋体">三、申报新专业程序</span></span></strong> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:24px;margin-left:24px;text-indent:32px;text-align:justify;text-justify:inter-ideograph;line-height:23px"><span style=";font-family:宋体;font-size:16px">1、二级教学单位对拟申报专业的教师基本情况、办学条件进行充分论证，填写《普通高等学校本科专业设置申请表（备案专业适用）》，在规定时间内将《申请表》报送教务处。</span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:24px;margin-left:24px;text-indent:32px;text-align:justify;text-justify:inter-ideograph;line-height:23px"><span style=";font-family:宋体;font-size:16px">2、教务处组织各拟申报新专业进行答辩，由校学术委员会委托下设的教学指导委员会进行评议和投票，确定学校拟增设的新专业名单，报请主管校领导审定。</span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:24px;margin-left:56px;line-height:21px"><span style=";font-family:宋体;font-size:16px">3、将拟新增专业名单提交校长办公会核准。4、将审定通过的拟增设新专业的相关材料上传至教育部新专业申报网站，</span> </p><p style="line-height:9px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-left:24px;line-height:18px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">公示期为一个月。</span></span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:24px;margin-left:24px;text-indent:32px;line-height:21px"><span style=";font-family:宋体;font-size:16px">5、公示期满后，学校将公示期间所提意见的研究处理情况及专业设置申请材料报教育厅。</span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:24px;margin-left:24px;text-indent:32px;line-height:21px"><span style=";font-family:宋体;font-size:16px">6、教育厅对我校提供的专业备案材料、公示期间所提意见、我校研究处理情况等进行审核，将相关材料报教育部备案。</span> </p><p><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"><br/> </span> &nbsp; &nbsp;</p><p style="line-height:13px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="line-height:13px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="line-height:18px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style=";text-align:center"><span style=";font-family:Times;font-size:16px">88</span> </p><p><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"><br/> </span> &nbsp;</p><p style="line-height:5px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span></p><p style="margin-left:56px;line-height:18px"><span style=";font-family:宋体;font-size:16px">7、教育部公布备案结果。</span> </p><p style="line-height:19px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span></p><p style="margin-left:56px;line-height:18px"><strong><span style=";font-family:宋体;font-weight:bold;font-size:16px"><span style="font-family:宋体">四、其他说明</span></span></strong> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span></p><p style="margin-right:24px;margin-left:24px;text-indent:32px;line-height:21px"><span style=";font-family:宋体;font-size:16px">1、申报设置国家控制布点专业，按以上申报有关程序和要求报送材料至教育厅，经教育厅报送至教育部后，由教育部公布审批结果。</span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span></p><p style="margin-right:24px;margin-left:24px;text-indent:32px;line-height:21px"><span style=";font-family:宋体;font-size:16px">2、对于尚未列入《专业目录》的新专业，需要填写《普通高等学校本科专业设置申请表（审批专业适用）》，其它程序同控制布点专业。</span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span></p><p style="margin-right:24px;margin-left:24px;text-indent:32px;line-height:21px"><span style=";font-family:宋体;font-size:16px">3、严格控制年度专业增设总量。全校年度申报新专业一般不超过 3 个，上年度已经成功申报新专业的二级教学单位原则上不能连续申报。</span> </p><p style="line-height:19px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span></p><p style="margin-left:56px;line-height:18px"><strong><span style=";font-family:宋体;font-weight:bold;font-size:16px"><span style="font-family:宋体">五、申报新专业的材料要求</span></span></strong> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span></p><p style="margin-right:16px;margin-left:24px;text-indent:32px;line-height:23px"><span style=";font-family:宋体;font-size:16px">1、申报目录内专业（含国家控制布点专业）填写《普通高等学校本科专业设置申请表（备案专业适用）》一式二份，《专业设置（备案专业）申请汇总表》一式二份，均用 A4 纸打印。</span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span></p><p style="margin-right:24px;margin-left:24px;text-indent:32px;text-align:justify;text-justify:inter-ideograph;line-height:23px"><span style=";font-family:宋体;font-size:16px">2、申报目录外专业填写《普通高等学校本科专业设置申请表（审批专业适用）》一式二份，《专业设置（审批专业）申请汇总表》一式二份，均用 A4 纸打印。</span> </p><p style="line-height:8px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span></p><p><span style=";font-family:宋体;font-size:16px">3、用于佐证新专业申报的各类支撑材料。</span></p><p><span style="font-size: 24px; color: rgb(255, 0, 0);"><br/></span><br/></p>', 1);
INSERT INTO uml_open_course_db.dguterd1_uml (erd1_id, erd1_title, erd1_type, erd1_date, erd1_origin, erd1_content, erd1_state) VALUES (10018, '东莞理工学院校级教学成果奖培育项目的遴选和培育办法 ', '教学手段', '2018-12-05', '东莞理工学院教务处  ', '<p style="text-align: center;"><span style="color: rgb(255, 0, 0); font-size: 24px;">东莞理工学院校级教学成果奖培育项目的遴选和培育办法</span></p><p><span style="color: rgb(255, 0, 0); font-size: 24px;"><span style="color: rgb(0, 0, 0); font-size: 18px;">为推进学校教育教学改革，加强本科教学质量与教学改革工程建设，更好地总结我校教育教学改革成果与经验，迎接下一轮的教学成果奖评定，学校开展校教学成果奖培育项目的遴选和培育。 <br/> <br/> <br/> 一、培育项目的遴选原则 <br/> <br/> 1、以人才培养模式的改革和创新为重点，积极推动解决学校在应用型人才培养过程中的问题，培养符合地方经济社会发展需要的各方面人才。 <br/> <br/> 2、鼓励与政府、企业、行业和科研院所的积极合作，重点培育在产学研结合基础上具有良好推广应用效果的项目。 <br/> <br/> 3、培育项目不能仅仅是对已有教改项目或成果的堆砌，而应在梳理整合的基础上，提出具有明确改革意识和建设思路、创新性强、特色明显、预期效果好，代表本单位人才培养工作特色，能对学校甚至全省高校起示范辐射作用的项目。 <br/> <br/> <br/> 二、培育项目范围 <br/> <br/> 1、针对教育对象的特点和人才培养的要求，运用现代教育思想和教学手段，在开展课程、教材、实验、实习基地建设等方面，探索教学规律，更新教学内容，改进教学方法，提高教学水平和教育质量的成果。 <br/> <br/> 2、根据教育目的、教育环境和教育教学规律，在组织教学工作，推动教学改革，开展教学评估，加强专业（学科）、教师队伍和学风建设，促进产学研相结合，实现教学管理现代化等方面的成果。 <br/> <br/> 3、对已有国家级、省部级和校级项目立项和支撑背景的（以正式公布文件为准，包括教学类项目、实验教学示范中心、教学团队、特色专业、精品课程和教学名师等），依托该项目已开展相关教改研究与实践，不以资金资助为主要目的但尚未推荐申报各级教学成果奖的优先考虑。 <br/> <br/> 4、已获校级、省级教学成果奖的项目在原成果基础上再进行深化改革实践,具有较大推广价值与应用前景，有更高层次或更高级别获奖可能性的项目，可以申报校级教学成果奖培育项目。 <br/> <br/> <br/> 三、遴选条件 <br/> <br/> 1、申报项目应有 2 年以上教育教学实践检验，实践检验的起始时间应从正 <br/> <br/> <br/> <br/> 90 <br/> <br/> <br/> 式实施（包括正式试行）教育教学方案的时间开始计算，不含研讨、论证和制定方案的时间。截止时间为推荐省级、国家级教学成果奖的时间。 <br/> <br/> 2、项目负责人应具有副教授以上职称，具备较强的教学研究能力和教学实践经验，曾承担校级以上教学研究改革项目或获得校级以上教学成果奖。项目研究队伍实力强、结构合理，项目组成员不超过 5 人。 <br/> <br/> 3、以协同创新为导向的跨专业、跨学科、跨单位联合申报项目，是学校教育教学改革中重点培育的方向。 <br/> <br/> <br/> 四、遴选程序 <br/> <br/> 1、项目主持人负责填写《东莞理工学院教育教学成果奖培育项目申请书》； <br/> <br/> 2、各院系组织人员申报项目，由所在单位初审，填写推荐意见和《东莞理工学院教学成果奖培育项目申报汇总表》后，向学校推荐申报。 <br/> <br/> 3、学校组织专家对申报项目进行遴选，评审出校级教学成果奖培育项目。 <br/> <br/> <br/> 五、培育办法 <br/> <br/> 1、对符合校级教学成果奖申报要求、解决教学问题成效显著、创新点突出、具有一定推广应用价值和竞争实力的项目给予一定的经费资助，并在政策上给予支持和倾斜。 <br/> <br/> 2、培育项目确定后，教务处将定期对项目建设过程进行指导交流和督促检查，确保培育成效。 <br/> <br/> 3、在评选校级及以上各级教育教学成果奖评奖中对培育项目优先考虑</span><br/></span></p>', 1);
INSERT INTO uml_open_course_db.dguterd1_uml (erd1_id, erd1_title, erd1_type, erd1_date, erd1_origin, erd1_content, erd1_state) VALUES (10020, '东莞理工学院专业建设经费使用管理办法', '教学方式', '2018-12-05', '东莞理工学院教务处', '<p style="margin-right:11px;text-align:center;line-height:21px"><span style="font-size: 24px; color: rgb(255, 0, 0);"><strong><span style="font-size: 24px; font-weight: bold; font-family: 宋体;">东莞理工学院专业建设经费使用管理办法</span></strong></span></p><p style="margin-right: 11px; line-height: 21px; text-align: center;"><strong><span style=";font-family:宋体;font-weight:bold;font-size:19px"><span style="font-family:宋体">第一章</span></span></strong> <strong><span style=";font-family:宋体;font-weight:bold;font-size:18px"><span style="font-family:宋体">总则</span></span></strong> </p><p style="line-height:18px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:31px;margin-left:21px;text-indent:31px;text-align:justify;text-justify:inter-ideograph;line-height:23px"><strong><span style=";font-family:宋体;font-weight:bold;font-size:16px"><span style="font-family:宋体">第一条</span> </span></strong><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">为进一步加强学校专业建设经费管理，提高专业建设效益，根据《东莞理工学院教学经费管理办法》《东莞理工学院教学专项经费使用专项经费使用暂行办法》，结合学校实际，特制订本办法。</span></span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:28px;margin-left:21px;text-indent:31px;text-align:justify;text-justify:inter-ideograph;line-height:23px"><strong><span style=";font-family:宋体;font-weight:bold;font-size:16px"><span style="font-family:宋体">第二条</span> </span></strong><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">专业建设费指学校下达的普通专业建设费、卓越计划专业建设费、模式改革实验班专业建设费和专业认证建设经费。专门立项的各种专业建设经费参照执行。</span></span> </p><p style="line-height:13px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="line-height:24px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:11px;text-align:center;line-height:21px"><strong><span style=";font-family:宋体;font-weight:bold;font-size:19px"><span style="font-family:宋体">第二章</span></span></strong> <strong><span style=";font-family:宋体;font-weight:bold;font-size:18px"><span style="font-family:宋体">经费管理</span></span></strong> </p><p style="line-height:18px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:24px;margin-left:21px;text-indent:31px;text-align:justify;text-justify:inter-ideograph;line-height:23px"><strong><span style=";font-family:宋体;font-weight:bold;font-size:15px"><span style="font-family:宋体">第三条</span> </span></strong><span style=";font-family:宋体;font-size:15px"><span style="font-family:宋体">专业建设经费使用和管理坚持</span>“谁使用、谁负责”的原则。学校为经费使用管理的责任单位，教务处负责实施；二级学院教学副院长负责经费管理，由院长、教学副院长“双签”审批，系主任或专业负责人负责经费具体执行。</span> </p><p style="line-height:8px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-left:52px;line-height:18px"><strong><span style=";font-family:宋体;font-weight:bold;font-size:16px"><span style="font-family:宋体">第四条</span></span></strong> <span style=";font-family:宋体;font-size:15px"><span style="font-family:宋体">专业建设经费预算纳入学校年度预算，专项管理，专款专用。</span></span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:31px;margin-left:21px;text-indent:31px;text-align:justify;text-justify:inter-ideograph;line-height:24px"><strong><span style=";font-family:宋体;font-weight:bold;font-size:16px"><span style="font-family:宋体">第五条</span> </span></strong><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">专业建设经费用于支持专业建设目标要求的教师发展与教学团队建设、课程教材资源开发、实验实训条件建设、学生创业创新训练、国内外教学交流合作、教育教学研究与改革以及专业评估认证等所需要的必要支出，主要范围包括：</span></span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:28px;margin-left:21px;text-indent:24px;text-align:justify;text-justify:inter-ideograph;line-height:23px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">（一）教师发展与教学团队建设经费：用于专业带头人、骨干教师和青年教师的培养培训，包括教师进修费、差旅费、住宿费、培训费、校外专家咨询费、劳务费等。</span></span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:31px;margin-left:21px;text-indent:24px;text-align:justify;text-justify:inter-ideograph;line-height:23px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">（二）课程教材资源开发经费：用于课程、教材、教案及教学视频的开发、制作与共享，包括课程制作费、教材出版费、图书购置费、资料费、专用软件购买费、文献检索费、信息化教学所需的系统开发费用。</span></span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:23px;margin-left:21px;text-indent:24px;text-align:justify;text-justify:inter-ideograph;line-height:24px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">（三）实验实训条件建设经费：用于改善本科生实验实训条件，包括专用材料和设备购置费、设备升级和维修费、实验室维修改造费、校外仪器设备租用费、测试化验加工和计算分析费。设备采购按学校规定执行。用于实验实训条件建设支出的比例不得超过专业建设经费的</span> 30%。</span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:31px;margin-left:21px;text-indent:24px;text-align:justify;text-justify:inter-ideograph;line-height:24px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">（四）学生创业创新训练经费：用于提升本科生专业技能和创业创新能力，如各种实习实训实践、项目引导实施、科技创新和竞赛活动等产生的费用，包括实验材料购置费、学生租车费、学生差旅费、必要的竞赛报名费、学生竞赛奖励费、外聘人员指导费等。</span></span> </p><p><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"><br/> </span> &nbsp; &nbsp;</p><p style="line-height:13px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="line-height:13px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="line-height:16px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style=";text-align:center"><span style=";font-family:Times;font-size:16px">95</span> </p><p><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"><br/> </span> &nbsp; &nbsp;</p><p style="line-height:8px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:31px;margin-left:21px;text-indent:24px;text-align:justify;text-justify:inter-ideograph;line-height:24px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">（五）国内外教学交流合作经费：用于国内外高校、研究机构、企事业单位交流教学管理经验和教学研究成果，联合培养本科生、共建品牌专业项目过程中发生的费用，包括差旅费、会议费，国（境、校）外专家旅费、伙食费及工作酬金（补贴）等。</span></span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:31px;margin-left:21px;text-indent:24px;text-align:justify;text-justify:inter-ideograph;line-height:23px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">（六）教育教学研究与改革经费：用于提高本科生培养质量而开展的教育教学研究与改革的活动，包括调研或参会产生的差旅交通费、会议费、版面费，校外专家咨询费、劳务费等。</span></span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:31px;margin-left:21px;text-indent:24px;text-align:justify;text-justify:inter-ideograph;line-height:21px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">（七）专业评估认证经费：专业文化环境建设、文档资料建设、外请评估专家、专业认证产生的材料购置费、差旅交通费、咨询费和认证费等。</span></span> </p><p style="line-height:8px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-left:44px;line-height:18px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">（八）人才培养方案承诺的培养环节实施所需的其他费用。</span></span> </p><p style="line-height:8px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-left:44px;line-height:18px"><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">以上各项经费中交通费用的支出不得超过总额度的</span> 15%。</span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:31px;margin-left:21px;text-indent:31px;text-align:justify;text-justify:inter-ideograph;line-height:21px"><strong><span style=";font-family:宋体;font-weight:bold;font-size:16px"><span style="font-family:宋体">第六条</span> </span></strong><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">专业建设经费形成的仪器设备、课程教材资源等，应积极予以开放共享，提高利用效率。</span></span> </p><p style="line-height:13px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="line-height:24px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:10px;text-align:center;line-height:21px"><strong><span style=";font-family:宋体;font-weight:bold;font-size:19px"><span style="font-family:宋体">第三章</span></span></strong> <strong><span style=";font-family:宋体;font-weight:bold;font-size:18px"><span style="font-family:宋体">绩效考核和监督检查</span></span></strong> </p><p style="line-height:18px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:31px;margin-left:21px;text-indent:31px;text-align:justify;text-justify:inter-ideograph;line-height:23px"><strong><span style=";font-family:宋体;font-weight:bold;font-size:15px"><span style="font-family:宋体">第七条</span> </span></strong><span style=";font-family:宋体;font-size:15px"><span style="font-family:宋体">教务处负责专业建设的过程指导管理和年度验收，按照项目建设效益、经费执行度等实际情况安排下一年度预算；对管理规范、实施成效显著的专业给予适当奖励性投入；对管理松弛、成效较差的专业酌情减少资金投入。</span></span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:23px;margin-left:21px;text-indent:31px;line-height:23px"><strong><span style=";font-family:宋体;font-weight:bold;font-size:16px"><span style="font-family:宋体">第八条</span> </span></strong><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">各二级学院应根据年度下达经费和开支范围合理编制预算报教务处、财务处备案，原则上不予调整，确因建设需要必须调整的，应提交书面申请，经审批后方可执行。</span></span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:30px;margin-left:21px;text-indent:31px;line-height:21px"><strong><span style=";font-family:宋体;font-weight:bold;font-size:15px"><span style="font-family:宋体">第九条</span> </span></strong><span style=";font-family:宋体;font-size:15px"><span style="font-family:宋体">专业建设费应根据经费开支范围和经费预算，按照国家和学校有关财务管理规定，本着节约高效的原则，合理使用建设经费，切实提高建设效益。</span></span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:28px;margin-left:21px;text-indent:31px;text-align:justify;text-justify:inter-ideograph;line-height:23px"><strong><span style=";font-family:宋体;font-weight:bold;font-size:16px"><span style="font-family:宋体">第十条</span> </span></strong><span style=";font-family:宋体;font-size:16px"><span style="font-family:宋体">各二级学院和专业负责人应遵守国家财政纪律，自觉接受财务处、审计处等二级组织机构的监督和检查。出国（境）差旅费、会议费、培训费、校外专家劳务费等开支标准参照国家及学校有关规定执行。</span></span> </p><p style="line-height:13px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="line-height:24px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:11px;text-align:center;line-height:21px"><strong><span style=";font-family:宋体;font-weight:bold;font-size:19px"><span style="font-family:宋体">第四章</span></span></strong> <strong><span style=";font-family:宋体;font-weight:bold;font-size:18px"><span style="font-family:宋体">附则</span></span></strong> </p><p style="line-height:15px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-left:52px;line-height:18px"><strong><span style=";font-family:宋体;font-weight:bold;font-size:16px"><span style="font-family:宋体">第十一条</span></span></strong> <span style=";font-family:宋体;font-size:15px"><span style="font-family:宋体">本办法由教务处、财务处负责解释。</span></span> </p><p style="line-height:11px"><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="margin-right:26px;margin-left:21px;text-indent:31px;line-height:21px"><strong><span style=";font-family:宋体;font-weight:bold;font-size:15px"><span style="font-family:宋体">第十二条</span> </span></strong><span style=";font-family:宋体;font-size:15px"><span style="font-family:宋体">本办法自发文之日起试行，原《东莞理工学院教学专项经费使用暂行办法》（莞工〔</span>2015〕32 号）中有关专业建设经费使用办法条款同时废止。</span> </p><p><span style=";font-family:&#39;Times New Roman&#39;;font-size:13px"><br/> </span></p><p><br/></p>', 1);
