  collate = utf8_esperanto_ci;

INSERT INTO uml_open_course_db.social_evalution (evalutionId, evalutionTitle, content, evalutionTime, status) VALUES (1, '海王：提前两周的失望', '<p><span style="font-size: 1px;">昨天晚上看完海王全球首发，我是一脸懵逼的，从Batman begin开始看了所有DC漫威的电影首映的我 其震撼程度仅仅次于绿灯侠，当然，是不好的震撼。期待了整整大半年的我感觉吃了慢慢一口热翔，失望二字不足以描述我当时观影的心情，看完以后我脑海里没有回想任何一处电影的画面，闪过的全是当年绿灯侠回忆。是的，我对海王的感觉，是和绿灯侠一档的，但绝对比绿灯侠要好一点，毕竟是领先了八年的CG技术，还有温子仁导演动作场面的调度还是很棒的，但是在剧情走向，世界观的塑造，人物的塑造 还有其想要探讨和表达的信息，都是幼稚园级别的 再上豆瓣再看看别人的评价我的下巴简直掉到了亚特兰蒂斯深海里 普遍的评价是 爽 牛逼 DC崛起了 DCEU最好的电影 让我再也沉不住气 也想来说一说这海王到底烂在哪里。</span><br></p>', '2018-12-26 18:34:49', 1);
INSERT INTO uml_open_course_db.social_evalution (evalutionId, evalutionTitle, content, evalutionTime, status) VALUES (2, '海王分析', '<p><span style="font-size: 1px;">在进入具体分析海王这部电影之前，我想先说明一下，我不是漫威粉，我只是一个爱看电影，特别爱看超级英雄电影的影迷。我从电影认识的美漫，甚至从诺兰的黑暗骑士三部曲开始，我更喜欢DC的世界观。因为相比漫威贴近与现实生活，DC塑造了一个新的世界，这就是人们所说的史诗感 我对别人对DC电影评价为暗黑风一直不置可否，DC的电影只是更有深度，更想探讨一下更深层次的东西 这是DC本身漫画风格的一种延续，而诺兰的黑暗骑士三部曲，和扎导的守望者很好的保留了这种传统，并完成了这四部到目前为止我依然认为是最好的四部超级英雄电影。接下来漫威和DC分别开启了MCU和DCEU，完完全全改变了超级英雄电影的拍摄方式和评价标准。我们现在先回到海王这部电影。</span></p>', '2018-12-26 18:44:05', 0);
INSERT INTO uml_open_course_db.social_evalution (evalutionId, evalutionTitle, content, evalutionTime, status) VALUES (3, '《海王》隐藏彩蛋与漫画关联终极分析', '<p><span style="font-size: 1px;">主世界的海王（亚瑟·库瑞）初次登场于1956年《冒险漫画》（Adventure Comics）第229期。由灯塔看守人汤姆·库瑞和亚特兰娜所生，由于母亲的亚特兰蒂斯血统，亚瑟从小就有在水中呼吸和与海洋生物交流等能力。在父亲的教导下，亚瑟决定成为海洋的守护者，并改用“海王”（Aquaman）这个称号。</span><br></p>', '2018-12-26 18:44:35', 1);
INSERT INTO uml_open_course_db.social_evalution (evalutionId, evalutionTitle, content, evalutionTime, status) VALUES (4, '起源', '<p><span style="font-size: 1px;">海王的起源还有其它不同的版本，比如在无限地球危机事件后的Aquaman Special（1989）中，海王为巫师亚特兰（不是国王）与女王亚特兰娜所生，因其天生的金发而被迷信的亚特兰蒂斯人视为不祥（实为国王喜当爹），被抛弃在礁石上，后来遇到灯塔看守人汤姆·库瑞并被其收养。</span><br></p><p><span style="font-size: 1px;">版权归作者所有，任何形式转载请联系作者。</span></p><p><span style="font-size: 1px;">作者：彩蛋君KL（来自豆瓣）</span></p><p><span style="font-size: 1px;">来源：<a href="https://movie.douban.com/review/9805850/" rel="nofollow">https://movie.douban.com/review/9805850/</a></span></p>', '2018-12-26 18:45:03', 0);
INSERT INTO uml_open_course_db.social_evalution (evalutionId, evalutionTitle, content, evalutionTime, status) VALUES (5, '海王起源', '<p><span style="font-size: 1px;">版权归作者所有，任何形式转载请联系作者。</span></p><p><span style="font-size: 1px;">作者：彩蛋君KL（来自豆瓣）</span></p><p><span style="font-size: 1px;">来源：<a href="https://movie.douban.com/review/9805850/" rel="nofollow">https://movie.douban.com/review/9805850/</a></span></p><p><span style="font-size: 1px;">海王是美国正义联盟（JLA）的创始人之一，与超人、蝙蝠侠、神奇女侠、闪电侠、绿灯侠和火星猎人并称为正义联盟“七巨头”。历史上正义联盟经历过多次成员重组，海王是联盟中地位相对巩固的一员，更一度当上联盟的主席。</span></p>', '2018-12-26 18:45:34', 0);
INSERT INTO uml_open_course_db.social_evalution (evalutionId, evalutionTitle, content, evalutionTime, status) VALUES (6, '只要能和鱼说话，走遍天下都不怕', '<p><span style="font-size: 1px;">版权归作者所有，任何形式转载请联系作者。</span></p><p><span style="font-size: 1px;">作者：凌睿（来自豆瓣）</span></p><p><span style="font-size: 1px;">来源：<a href="https://movie.douban.com/review/9805020/" rel="nofollow">https://movie.douban.com/review/9805020/</a></span></p><p><span style="font-size: 1px;"><br></span><br></p><p><span style="font-size: 1px;"><br></span><br></p><p><span style="font-size: 1px;">江湖上流传着这样一个传说：长得像徐锦江的男孩，运气都不会太差。</span></p><p><span style="font-size: 1px;"><br></span><br></p><p><span style="font-size: 1px;">而我们的主人公就是这样一个人。</span></p><p><span style="font-size: 1px;"><br></span><br></p><p><span style="font-size: 1px;">他，是一个长得很像徐锦江的超级英雄。他是个大胡子，不喜欢穿衣服。他肌肉发达，刀枪不入。</span></p><p><span style="font-size: 1px;"><br></span><br></p><p><span style="font-size: 1px;">他是王后的儿子，不过他的国王路并不顺畅，因为他的弟弟总是跟他争夺王位。他的弟弟仇恨人类，但是颜值很高，所以仍然圈粉无数。</span></p><p><span style="font-size: 1px;"><br></span><br></p><p><span style="font-size: 1px;">他还有个强大兵器，谁都拿不走只有他能拿走。</span></p><p><span style="font-size: 1px;"><br></span><br></p><p><span style="font-size: 1px;">没错，他就是雷神。</span></p>', '2018-12-26 18:46:03', 0);
