INSERT INTO uml_open_course_db.teacherTeam (id, teacher_name, teacher_message, isLecturer) VALUES (1, '侯爱民', '<p><span style="color: rgb(68, 68, 68); font-family: &quot;Times New Roman&quot;; font-size: 14px; text-align: justify; background-color: rgb(255, 255, 255);">侯爱民，男，976年3月出生。籍贯江西南昌。东莞理工学院计算机科学与技术学院讲师。研究生学历，博士学位。</span><br/><span style="color: rgb(68, 68, 68); font-family: &quot;Times New Roman&quot;; font-size: 14px; text-align: justify; background-color: rgb(255, 255, 255);">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;从2017-2018学年第1学期开始，至2018-2019学年第1学期止，总共为2届的学生讲授《UML面向对象分析与设计》课程。累计学生人数200，累计学时120。11</span></p>', 1);
INSERT INTO uml_open_course_db.teacherTeam (id, teacher_name, teacher_message, isLecturer) VALUES (2, ' 周鹏', '<p>周鹏123，男，1976年3月出生。籍贯湖南长沙。东莞理工学院计算机科学与技术学院讲师。研究生学历，博士学位。 &nbsp; &nbsp; &nbsp; &nbsp;从2017-2018学年第1学期开始，至2018-2019学年第1学期止，总共为2届的学生讲授《面向对象分析与设计(UML)》、《软件需求分析与设计》课程。累计学生人数200，累计学时120。afafda</p>', 1);
