INSERT INTO uml_open_course_db.teacher_evalution (evalutionId, evalutionTitle, teacherName, content, evalutionTime, status) VALUES (5, '对小明老师上课特点的评价', '小明老师', '<p><a href="http://sports.qq.com/a/20181225/010930.htm" target="_blank">欧联杯冠军主帅执教申花 吴金贵任体育总监</a></p><p><a href="https://sports.qq.com/a/20181225/011870.htm" target="_blank">揭秘申花新帅：号称杯赛之王 执教履历多高开低走</a></p><p><a href="https://sports.qq.com/a/20181225/011642.htm" target="_blank">申花换帅像是一个问号 “青春风暴”会夭折么？</a></p><p><a href="https://sports.qq.com/a/20181225/011695.htm" target="_blank">19赛季中超仅剩3土帅 李霄鹏李铁如何捍卫尊严</a></p><p><a href="http://sports.qq.com/a/20181226/000805.htm" target="_blank">他要终结梅罗时代！足坛身价第1人 姆巴佩的天选人生</a></p><p><a href="http://view.inews.qq.com/a/20181226A0BNEO00" target="_blank">女排17岁小将初生牛犊不怕虎 蔡斌又挖掘出一颗新星</a></p>', '2018-12-26 17:52:19', 0);
INSERT INTO uml_open_course_db.teacher_evalution (evalutionId, evalutionTitle, teacherName, content, evalutionTime, status) VALUES (6, '对小明老师上课特点的评价', '小明老师', '<p><a href="http://sports.qq.com/a/20181225/010930.htm" target="_blank">欧联杯冠军主帅执教申花 吴金贵任体育总监</a></p><p><a href="https://sports.qq.com/a/20181225/011870.htm" target="_blank">揭秘申花新帅：号称杯赛之王 执教履历多高开低走</a></p><p><a href="https://sports.qq.com/a/20181225/011642.htm" target="_blank">申花换帅像是一个问号 “青春风暴”会夭折么？</a></p><p><a href="https://sports.qq.com/a/20181225/011695.htm" target="_blank">19赛季中超仅剩3土帅 李霄鹏李铁如何捍卫尊严</a></p><p><a href="http://sports.qq.com/a/20181226/000805.htm" target="_blank">他要终结梅罗时代！足坛身价第1人 姆巴佩的天选人生</a></p><p><a href="http://view.inews.qq.com/a/20181226A0BNEO00" target="_blank">女排17岁小将初生牛犊不怕虎 蔡斌又挖掘出一颗新星</a></p>', '2018-12-26 17:53:00', 0);
INSERT INTO uml_open_course_db.teacher_evalution (evalutionId, evalutionTitle, teacherName, content, evalutionTime, status) VALUES (7, 'GitHub的发展史', '小康老师', '<p><span style="color: rgb(73, 73, 73); font-size: 13px;">海底世界戏份多到超出想象，这是一部主打海洋英雄的片应有的样子，一本满足。撒哈拉沙漠、西西里岛、海沟族追逐戏、地心藏海…奔波全球海洋奇观，上天入地，美不胜收。温子仁手下的DC，不再暗黑，情节跌宕又完整，正如他的恐怖片一样吸人，吸入深海底。亚特兰蒂斯与亚瑟的历史传说，完美融入超级英雄剧情，陆地之子抑或海洋之王，海王不止粗汉也有柔情，三叉戟秒变金箍棒，这不就是《西游记之大闹龙宫》？</span><br></p>', '2018-12-26 17:55:53', 0);
INSERT INTO uml_open_course_db.teacher_evalution (evalutionId, evalutionTitle, teacherName, content, evalutionTime, status) VALUES (8, 'git pull会把本地未提交修改覆盖', '东方不败', '<p><span style="color: rgb(36, 41, 46); font-size: 16px;">所有的Servlet继承这个BaseServlet，继承之后，不用像以前一样实现doget dopost等方法，只需要在里面实现你自己想要的方法即可， 在超链接里面想要调用servlet里面的方法时，像之前丽萍哥教的那样，超链接需要额外加一个method参数，参数的值就是你想要调用的方法名。 此外，servlet中自己定义的函数返回值都为String类型，返回值就是该方法调用完之后想要跳转的页面。</span><br></p>', '2018-12-28 09:22:59', 0);
